sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	// Modified the if to prevent unnecessary trips to the server and only return if:
	//   - The form isLoading and not isNewRecord :: Allow email to auto-fill when requestor is auto-populated on new record
	//   - newValue == oldValue and the form isLoading :: Allow email to auto-fill when requestor is the oldValue, 
	//     is changed and then changed back before saving 

	if ((isLoading &! g_form.isNewRecord) || newValue === '' || (newValue == oldValue && isLoading)) {
		return;
	}

	// alert('Querying server'); //Used to verify server is only being contacted when expected

	// Instantiate the GetEmailAddress Script Include
	var getEmailAddr = new GlideAjax('GetEmailAddress');
	// Specify the getEmail address
	getEmailAddr.addParam('sysparm_name','getEmail');
	// Pass the Requested for sys_id
	getEmailAddr.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
	// Send the request to the server
	getEmailAddr.getXML(populateEmailField);

	// When the response is back from the server
	function populateEmailField(response){
		//Extract the email address from the response, clear any value from the email field, and set the new value in the email field
		var emailFromScriptInclude = response.responseXML.documentElement.getAttribute('answer');
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email', emailFromScriptInclude);
		// Update the Requested for oldValue to the current selection to allow email addresses 
		// to update if switched back to the original value prior to saving
		oldValue = newValue;
	}



}
